const menuButton=document.querySelector(".burger-menu"),sideBar=document.querySelector(".app-sidebar"),cover=document.querySelector("#cover"),body=document.querySelector(".homepage");let menuOpen=!1;menuButton.addEventListener("click",()=>{menuOpen?(menuButton.classList.remove("open"),sideBar.classList.remove("open"),cover.classList.remove("cover"),body.classList.remove("open")):(menuButton.classList.add("open"),sideBar.classList.add("open"),cover.classList.add("cover"),body.classList.add("open")),menuOpen=!menuOpen}),cover.addEventListener("click",()=>{menuButton.classList.remove("open"),sideBar.classList.remove("open"),cover.classList.remove("cover"),body.classList.remove("open")});